// ── Montagem das categorias ───────────────────────────────────────────────
const categories = computed<Category[]>(() => [
    { id: 'shapes', name: 'Formas', items: basicShapes },
    { id: 'lines', name: 'Linhas & Setas', items: lines },
    { id: 'icons', name: 'Ícones', items: icons },
    { id: 'frames', name: 'Molduras', items: framesData },
    { id: 'grids', name: 'Grades', items: grids },
    { id: 'decoratives', name: 'Decorativos', items: decoratives },
])

// ── Busca ─────────────────────────────────────────────────────────────────
const allItems = computed(() =>
    categories.value.flatMap(c =>
        c.items.map(item => ({ ...item, categoryId: c.id, categoryName: c.name }))
    )
)

const filteredItems = computed(() => {
    const q = effectiveSearch.value.toLowerCase().trim()
    if (!q) return null
    return allItems.value.filter(item =>
        item.name.toLowerCase().includes(q) ||
        (item.tags || []).some(t => t.toLowerCase().includes(q)) ||
        item.categoryName.toLowerCase().includes(q)
    )
})

const activeCategoryObj = computed(() => {
    if (!activeCategory.value) return null
    return categories.value.find(c => c.id === activeCategory.value) || null
})

// ── Handlers ──────────────────────────────────────────────────────────────
const openCategory = (id: string) => { activeCategory.value = id }
const goBack = () => { activeCategory.value = null }
const insertElement = (item: ElementItem) => {
    emit('insert-element', { type: item.type, data: item.data })
}
